var isArrayNullOrWhiteSpace = function (obj) {
    var checkArray = [];
    if (obj.length > 0) {
        $.each(obj, function (index) {
            var val = obj[index];
            if (!val) {
                checkArray.push(val);
            }
        });
    }
    return checkArray.length > 0;
};

