CREATE FUNCTION audit.get_office_information_model(integer)
RETURNS TABLE
(
    office              text,
    logged_in_to        text,
    last_login_ip       text,
    last_login_on       TIMESTAMP WITH TIME ZONE,
    current_ip          text,
    current_login_on    TIMESTAMP WITH TIME ZONE,
    role                text,
