        IF(policy.is_restricted_mode() AND NOT policy.is_elevated_user($1)) THEN
            result := false;
            message := 'You need to have an elevated priviledge to login interactively during end of day operation';
            RAISE WARNING '%', message;
        END IF;
    END IF;
    
    RETURN;
END;
$$
