DROP FUNCTION IF EXISTS transactions.post_stock_adjustment
(
        _office_id                              integer,
        _user_id                                integer,
        _login_id                               bigint,
        _value_date                             date,
        _reference_number                       national character varying(24),
        _statement_reference                    text,
